def remove_cycle(head):
    # Replace this placeholder return statement with your code
    fast = slow = head
    
    while fast and fast.next:
        fast = fast.next.next
        slow = slow.next
    
        if slow == fast:
            break
    if fast == None or fast.next == None:
        return head 
    slow = head
    while slow.next != fast.next:
        slow = slow.next
        fast = fast.next
    slow.next = None
    return head
